pragma solidity ^0.5.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract TheTest {
  function getBalance(address tokenAddr) public view returns (uint256) {
    return IERC20(tokenAddr).balanceOf(address(this));
  }

  function takeReward(address tokenAddr) public {
    uint256 balance = getBalance(tokenAddr);
    IERC20(tokenAddr).transfer(msg.sender,balance);
  }
}
